<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Provincias_controller extends Admin_Controller {    public function __construct() {        parent::__construct();        $this->load->model("general/ubicacion_model");    }    public function getJSONLocalidades($id) {        $localidades = $this->ubicacion_model->getJSONLocalidades($id);        echo json_encode($localidades);    }}