<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Transferencia_Controller extends Admin_Controller {    public function __construct() {        parent::__construct();        $this->setTemplateDefault("admin/transferencia/templateTransferencia");        $this->validaPermiso("full pagos", "ver pagos");        $this->load->model("transferencia/transferenciamodel");    }    public function index() {        if ($this->post()) {            $this->transferenciamodel->insertaPago($this->post());            redirect('transferencias');        } else {            $data['transferencias'] = $this->transferenciamodel->getPagos();            $data['proveedores'] = $this->transferenciamodel->getAllCollection("Proveedor");            $data['condivas'] = $this->transferenciamodel->getAllCollection("Condiva");            $data['tipoPagos'] = $this->transferenciamodel->getAllCollection("Tipo_pago");            $data['bancos'] = $this->transferenciamodel->getAllCollection("Banco");            $data['footer'] = 'footer';            $this->loadTemplateDefault("index", $data);        }    }    public function borrar($id) {        $this->transferenciamodel->borrar($id);        redirect("transferencias");    }    public function getTransferenciasAjax() {        echo json_encode($this->transferenciamodel->getPagosAjax($_GET));    }    public function renglonesTransferenciaDocumento($pro_id) {        echo json_encode($this->transferenciamodel->renglonesPagoDocumentos($pro_id));    }    public function renglonesPagoTransferencia() {        echo json_encode($this->transferenciamodel->renglonesPagoDetalle());    }    public function getDocumentosTransferencia($pago_id) {        echo json_encode($this->transferenciamodel->getDocumentosPago($pago_id));    }    public function getDetallesTransferencia($pago_id) {        echo json_encode($this->transferenciamodel->getDetallesPago($pago_id));    }    public function getTransferenciaById($pago_id) {        echo json_encode($this->transferenciamodel->getPagoByIdJson($pago_id));    }}