<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** * Condiva * * @Table(name="condiva") * @Entity */class Condiva {    /**     * @var integer $id     *     * @Column(name="condiva_id", type="integer", nullable=false)     * @Id     * @GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @var string $nombre     *     * @Column(name="condiva_nombre", type="string")     */    private $nombre;    /**     * @var integer $activo     *     * @Column(name="condiva_activo", type="integer")     */    private $activo;    /**     * @var integer $importe_iva     *     * @Column(name="condiva_importe", type="decimal")     */    private $importe_iva;    function getId() {        return $this->id;    }    function getNombre() {        return $this->nombre;    }    function getImporte_iva() {        return $this->importe_iva;    }    function setImporte_iva($importe_iva) {        $this->importe_iva = $importe_iva;    }    function getActivo() {        return $this->activo;    }    function setId($id) {        $this->id = $id;    }    function setNombre($nombre) {        $this->nombre = $nombre;    }    function setActivo($activo) {        $this->activo = $activo;    }    public function __construct($nombre) {        $this->setActivo(1);        $this->actualizar($nombre);    }    public function actualizar($nombre) {        $this->setNombre($nombre);    }    function obtenerArray() {        $arreglo = array("id" => $this->id,            "nombre" => $this->nombre,            "activo" => $this->activo        );        return $arreglo;    }}