<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** * Provincia * * @Table(name="provincias") * @Entity */class Provincia {    /**     * @var integer $id     *     * @Column(name="id", type="integer", nullable=false)     * @Id     * @GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @var string $nombre     *     * @Column(name="nombre", type="string", length=200, nullable=true)     */    private $nombre;    function getId() {        return $this->id;    }    function getNombre() {        return $this->nombre;    }    function setId($id) {        $this->id = $id;    }    function setNombre($nombre) {        $this->nombre = $nombre;    }    function obtenerArray() {        $arreglo = array("id" => $this->id,            "nombre" => $this->nombre        );        return $arreglo;    }}