<?phpdefined('BASEPATH') OR exit('No direct script access allowed');/** * Rol * * @Table(name="rol") * @Entity */class Rol {    /**     * @var integer $id     *     * @Column(name="rol_id", type="integer", nullable=false)     * @Id     * @GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @var integer $peso     *     * @Column(name="rol_peso", type="integer", length=1, nullable=false)     */    private $peso;    /**     * @var string $nombre     *     * @Column(name="rol_nombre", type="string", length=32, nullable=false)     */    private $nombre;    /**     * @var string $controlador     *     * @Column(name="rol_panel", type="string", length=32, nullable=false)     */    private $controlador;    /**     * @var string $descripcion     *     * @Column(name="rol_descripcion", type="string", length=32, nullable=false)     */    private $descripcion;    /**     * @Column(type="datetime", name="rol_fecha_in", nullable=false)     */    protected $fechaCreacion;    /**     * @ManyToMany(targetEntity="Permiso")     * @JoinTable(name="rol_permiso",     *      joinColumns={@JoinColumn(name="rp_rol_id", referencedColumnName="rol_id")},     *      inverseJoinColumns={@JoinColumn(name="rp_permiso_id", referencedColumnName="permiso_id")}     *      )     */    private $permisos;    /**     * @ManyToMany(targetEntity="Usuario")     * @JoinTable(name="usuario_rol",     *      joinColumns={@JoinColumn(name="ur_rol_id", referencedColumnName="rol_id")},     *      inverseJoinColumns={@JoinColumn(name="ur_usuario_id", referencedColumnName="usuario_id")}     *      )     */    private $usuarios;    function getControlador() {        return $this->controlador;    }    function setControlador($controlador) {        $this->controlador = $controlador;    }        function getUsuarios() {        return $this->usuarios;    }    function setUsuarios($usuarios) {        $this->usuarios = $usuarios;    }    function getId() {        return $this->id;    }    function getPeso() {        return $this->peso;    }    function getNombre() {        return $this->nombre;    }    function getDescripcion() {        return $this->descripcion;    }    function getFechaCreacion() {        return $this->fechaCreacion;    }    function getPermisos() {        return $this->permisos;    }    function setId($id) {        $this->id = $id;    }    function setPeso($peso) {        $this->peso = $peso;    }    function setNombre($nombre) {        $this->nombre = $nombre;    }    function setDescripcion($descripcion) {        $this->descripcion = $descripcion;    }    function setFechaCreacion($fechaCreacion) {        $this->fechaCreacion = $fechaCreacion;    }    function setPermisos($permisos) {        $this->permisos = $permisos;    }    public function __construct() {        $this->permisos = new Doctrine\Common\Collections\ArrayCollection();        $this->usuarios = new Doctrine\Common\Collections\ArrayCollection();        $this->setPeso(1);    }    public function actualizar($nombre, $descripcion, $peso = 1) {        $this->setNombre($nombre);        $this->setDescripcion($descripcion);        $this->setPeso($peso);    }}